// >>> histogram(("b b b b a"))
// (std::map<std::string,long>({{"b", 4}}))
// >>> histogram((""))
// (std::map<std::string,long>())
std::map<std::string,long> histogram(std::string test) {
    std::map<std::string,long> result;
    std::unordered_map<std::string,long> count;
    std::stringstream ss(test);
    std::string word;
    while (ss >> word) {
        count[word]++;
    }
    long max_count = 0;
    for (auto& [k,v]: count) {
        if (v > max_count) {
            max_count = v;
            result.clear();
            result[k] = v;
        } else if (v == max_count) {
            result[k] = v;
        }
    }
    return result;

