// >>> add_elements((std::vector<long>({(long)111, (long)21, (long)3, (long)4000, (long)5, (long)6, (long)7, (long)8, (long)9})), (4))
// (24)
// Constraints:
// 1. 1 <= len(arr) <= 100
// 2. 1 <= k <= len(arr)
long add_elements(std::vector<long> arr, long k) {
    long sum = 0;
    for (long i = 0; i < k; i++) {
        if (arr[i] < 10) {
            sum += arr[i];
        } else if (arr[i] < 100) {
            sum += arr[i];
        } else {
            sum += arr[i] % 100;
        }
    }
    return sum;

