#include<bits/stdc++.h>
// You are given a vector of numbers.
// You need to return the sum of squared numbers in the given vector,
// round each element in the vector to the upper int(Ceiling) first.
// Examples:
// >>> lst((std::vector<float>({(float)1.0f, (float)2.0f, (float)3.0f})))
// (14)
// >>> lst((std::vector<float>({(float)1.0f, (float)4.0f, (float)9.0f})))
// (98)
// >>> lst((std::vector<float>({(float)1.0f, (float)3.0f, (float)5.0f, (float)7.0f})))
// (84)
// >>> lst((std::vector<float>({(float)1.4f, (float)4.2f, (float)0.0f})))
// (29)
// >>> lst((std::vector<float>({(float)-2.4f, (float)1.0f, (float)1.0f})))
// (6)
long sum_squares(std::vector<float> lst) {
    long sum = 0;
    for(auto i:lst){
        sum += ceil(i)*ceil(i);
    }
    return sum;

