// Create a function which returns the largest index of an element which
// is not greater than or equal to the element immediately preceding it. If
// no such element exists then return -1. The given vector will not contain
// duplicate values.
// Examples:
// >>> can_arrange((std::vector<long>({(long)1, (long)2, (long)4, (long)3, (long)5})))
// (3)
// >>> can_arrange((std::vector<long>({(long)1, (long)2, (long)3})))
// (-1)
long can_arrange(std::vector<long> arr) {
    long n = arr.size();
    long max_index = -1;
    for (long i = n - 2; i >= 0; i--) {
        if (arr[i] < arr[i + 1]) {
            max_index = i;
            break;
        }
    }
    if (max_index == -1) {
        return -1;
    }
    long min_index = -1;
    long min_val = arr[n - 1];
    for (long i = n - 1; i > max_index; i--) {
        if (arr[i] < min_val) {
            min_index = i;
            min_val = arr[i];
        }
    }
    std::swap(arr[max_index], arr[min_index]);
    std::sort(arr.begin() + max_index + 1, arr.end());
    return max_index;

