// >>> order_by_points((std::vector<long>()))
// (std::vector<long>())
std::vector<long> order_by_points(std::vector<long> nums) {
    std::vector<std::pair<long, long>> nums_with_points;
    for (long num : nums) {
        long points = 0;
        while (num != 0) {
            points += num % 10;
            num /= 10;
        }
        nums_with_points.push_back({points, num});
    }
    std::sort(nums_with_points.begin(), nums_with_points.end(), [](const std::pair<long, long>& a, const std::pair<long, long>& b) {
        if (a.first == b.first) {
            return a.second < b.second;
        }
        return a.first < b.first;
    });
    std::vector<long> result;
    for (const auto& p : nums_with_points) {
        result.push_back(p.second);
    }
    return result;

