std::string find_max(std::vector<std::string> words) {
    std::string max_word = words[0];
    for (int i = 1; i < words.size(); i++) {
        int max_unique_chars = 0;
        int curr_unique_chars = 0;
        std::unordered_set<char> unique_chars;
        for (char c : words[i]) {
            if (unique_chars.find(c) == unique_chars.end()) {
                unique_chars.insert(c);
                curr_unique_chars++;
            }
        }
        if (curr_unique_chars > max_unique_chars) {
            max_unique_chars = curr_unique_chars;
            max_word = words[i];
        } else if (curr_unique_chars == max_unique_chars) {
            if (words[i] < max_word) {
                max_word = words[i];
            }
        }
    }
    return max_word;

