// (std::vector<long>({(long)2, (long)4, (long)6, (long)8}))
// >>> generate_integers((8), (2))
// (std::vector<long>({(long)2, (long)4, (long)6, (long)8}))
// >>> generate_integers((10), (14))
// (std::vector<long>())
std::vector<long> generate_integers(long a, long b) {
    std::vector<long> result;
    long min_num = std::min(a, b);
    long max_num = std::max(a, b);
    long min_even_digit = min_num % 10;
    long max_even_digit = max_num % 10;
    if (min_even_digit % 2 != 0) {
        min_even_digit += 2;
    }
    if (max_even_digit % 2 != 0) {
        max_even_digit -= 2;
    }
    if (min_even_digit > max_even_digit) {
        return result;
    }
    for (long i = min_even_digit; i <= max_even_digit; i += 2) {
        result.push_back(i);
    }
    return result;

