// ["()", "(())", "(()())"]
function separate_paren_groups(paren_string){
    paren_string = paren_string.replace(/\s/g, ''); // Remove spaces
    let result = [];
    let stack = [];
    let currentGroup = '';

    for (let char of paren_string) {
        if (char === '(') {
            stack.push(char);
            currentGroup += char;
        } else if (char === ')') {
            stack.pop();
            currentGroup += char;
            if (stack.length === 0) {
                result.push(currentGroup);
                currentGroup = '';
            }
        }
    }

    return result;
}
