//For a given array of input numbers, calculate Mean Absolute Deviation
// around the mean of this dataset.
// Mean Absolute Deviation is the average absolute difference between each
// element and a centerpoint (mean in this case):
// MAD = average | x - x_mean |
// >>> mean_absolute_deviation([1.0, 2.0, 3.0, 4.0])
// 1.0
function mean_absolute_deviation(numbers){
  var sum = 0;
  for(var i = 0; i < numbers.length; i++){
    sum += numbers[i];
  }
  var mean = sum / numbers.length;
  var mad = 0;
  for(var i = 0; i < numbers.length; i++){
    mad += Math.abs(numbers[i] - mean);
  }
  mad /= numbers.length;
  return mad;
}
//For a given array of input numbers, calculate Mean Absolute Deviation
// around the mean of this dataset.
// Mean Absolute Deviation is the average absolute difference between each
// element and a centerpoint (mean in this case):
// MAD = average | x - x_mean |
// >>> mean_absolute_deviation([1.0, 2.0, 3.0, 4.0])
// 1.0
function mean_absolute_deviation(numbers){
  var mean = numbers.reduce((a, b) => a + b, 0) / numbers.length;
  var mad = numbers.reduce((a, b) => a + Math.abs(b - mean), 0) / numbers.length;
  return mad;
}
