// >>> sum_product([])
// [0, 1]
// >>> sum_product([1, 2, 3, 4])
// [10, 24]
function sum_product(numbers){
    let sum = 0;
    let product = 1;
    for(let i = 0; i < numbers.length; i++){
        sum += numbers[i];
        product *= numbers[i];
    }
    return [sum, product];
}

