// [1, 2, 3]
// >>> sort_third([5, 6, 3, 4, 8, 9, 2])
// [2, 6, 3, 4, 8, 9, 5]
function sort_third(l){
    let sorted = l.filter((_, i) => i % 3 === 0).sort((a, b) => a - b);
    return l.map((e, i) => i % 3 === 0 ? sorted.shift() : e);
}
