// 13
// >>> prime_fib(5)
// 89
function prime_fib(n){
    function isPrime(num) {
        for(let i = 2, sqrt = Math.sqrt(num); i <= sqrt; i++)
            if(num % i === 0) return false; 
        return num > 1;
    }

    function fib(n) {
        if (n <= 1) return n;
        return fib(n-1) + fib(n-2);
    }

    let count = 0;
    let i = 0;
    while (true) {
        let fibNum = fib(i);
        if (isPrime(fibNum)) {
            count++;
            if (count === n) {
                return fibNum;
            }
        }
        i++;
    }
}
