// false
// >>> pairs_sum_to_zero([2, 4, -5, 3, 5, 7])
// true
// >>> pairs_sum_to_zero([1])
// false
function pairs_sum_to_zero(l){
    let seen = new Set();
    for(let i = 0; i < l.length; i++){
        if(seen.has(-l[i])){
            return true;
        }
        seen.add(l[i]);
    }
    return false;
}

