function fib4(n){
    if(n === 0 || n === 1 || n === 3){
        return 0;
    }
    if(n === 2){
        return 2;
    }
    let a = 0, b = 0, c = 2, d = 0;
    for(let i = 4; i <= n; i++){
        let temp = a + b + c + d;
        a = b;
        b = c;
        c = d;
        d = temp;
    }
    return d;
}

