//Return median of elements in the array l.
// >>> median([3, 1, 2, 4, 5])
// 3
// >>> median([-10, 4, 6, 1000, 10, 20])
// 15.0
function median(l){
    l.sort(function(a, b){return a - b});
    var len = l.length;
    if (len % 2 == 0) {
        return (l[len / 2 - 1] + l[len / 2]) / 2;
    } else {
        return l[(len - 1) / 2];
    }
}

