// false
// >>> same_chars("abcd", "dddddddabce")
// false
// >>> same_chars("eabcdzzzz", "dddzzzzzzzddddabc")
// false
function same_chars(s0, s1){
  let count = {};
  for(let char of s0) {
    count[char] = (count[char] || 0) + 1;
  }
  for(let char of s1) {
    if(!count[char]) {
      return false;
    }
    count[char]--;
  }
  for(let char in count) {
    if(count[char] !== 0) {
      return false;
    }
  }
  return true;
}

