//Return true is array elements are monotonically increasing or decreasing.
// >>> monotonic([1, 2, 4, 20])
// true
// >>> monotonic([1, 20, 4, 10])
// false
// >>> monotonic([4, 1, 0, -10])
// true
function monotonic(l){
    let inc = true;
    let dec = true;
    for(let i = 0; i < l.length - 1; i++){
        if(l[i] < l[i + 1]) dec = false;
        if(l[i] > l[i + 1]) inc = false;
    }
    return inc || dec;
}

