//Return sorted unique common elements for two arrays.
// >>> common([1, 4, 3, 34, 653, 2, 5], [5, 7, 1, 5, 9, 653, 121])
// [1, 5, 653]
// >>> common([5, 3, 2, 8], [3, 2])
// [2, 3]
function common(l1, l2){
  let set1 = new Set(l1);
  let set2 = new Set(l2);
  let commonElements = new Set([...set1].filter(x => set2.has(x)));
  return [...commonElements].sort((a, b) => a - b);
}
