//Return the largest prime factor of n. Assume n > 1 and is not a prime.
// >>> largest_prime_factor(13195)
// 29
// >>> largest_prime_factor(2048)
// 2
function largest_prime_factor(n){
    let largest = 2;
    while (n % 2 == 0) {
        n = n / 2;
    }
    for (let i = 3; i <= Math.sqrt(n); i += 2) {
        while (n % i == 0) {
            largest = i;
            n = n / i;
        }
    }
    if (n > 2) {
        largest = n;
    }
    return largest;
}

