function correct_bracketing(brackets){
    let stack = [];
    for(let i = 0; i < brackets.length; i++){
        if(brackets[i] === "("){
            stack.push(brackets[i]);
        }else if(brackets[i] === ")"){
            if(stack.length === 0){
                return false;
            }
            stack.pop();
        }
    }
    return stack.length === 0;
}

//brackets is a string of "(" and ")".
// return true if every opening bracket has a corresponding closing bracket.
// >>> correct_bracketing("(")
// false
// >>> correct_bracketing("()")
// true
// >>> correct_bracketing("(()())")
// true
// >>> correct_bracketing(")(()")
// false
function correct_bracketing(brackets){
    let count = 0;
    for(let i = 0; i < brackets.length; i++){
        if(brackets[i] === "("){
            count++;
        }else if(brackets[i] === ")"){
            count--;
        }
        if(count < 0){
            return false;
        }
    }
    return count === 0;
}

