// >>> fibfib(1)
// 0
// >>> fibfib(5)
// 4
// >>> fibfib(8)
// 24
function fibfib(n){
    if(n<2){
        return 0;
    }
    if(n===2){
        return 1;
    }
    let a=0,b=0,c=1,d;
    for(let i=3;i<=n;i++){
        d=a+b+c;
        a=b;
        b=c;
        c=d;
    }
    return d;
}

