// >>> search([1, 2, 2, 3, 3, 3, 4, 4, 4])
// 3
// >>> search([5, 5, 4, 4, 4])
// -1
function search(lst){
    let freq = {};
    lst.forEach(num => {
        if(freq[num]){
            freq[num]++;
        } else {
            freq[num] = 1;
        }
    });
    let max = -1;
    for(let key in freq){
        if(freq[key] >= key && Number(key) > max){
            max = Number(key);
        }
    }
    return max;
}

//You are given a non-empty array of positive integers. Return the greatest integer that is greater than 
// zero, and has a frequency greater than or equal to the value of the integer itself. 
// The frequency of an integer is the number of times it appears in the array.
// If no such a value exist, return -1.
// Examples:
// >>> search([4, 1, 2, 2, 3, 1])
// 2
// >>> search([1, 2, 2, 3, 3, 3, 4, 4, 4])
// 3
// >>> search([5, 5, 4, 4, 4])
// -1
function search(lst){
    let freq = {};
    lst.forEach(x => freq[x] ? freq[x]++ : freq[x] = 1);
    let result = -1;
    for(let key in freq){
        if(key <= freq[key] && key > result) result = key;
    }
    return result;
}

