//Write a function that returns true if the given number is the multiplication of 3 prime numbers
// and false otherwise.
// Knowing that (a) is less then 100. 
// Example:
// >>> is_multiply_prime(30)
// true
// 30 = 2 * 3 * 5
function is_multiply_prime(a){
    let count = 0;
    for(let i = 2; i < a; i++){
        if(a % i == 0){
            count++;
        }
    }
    return count == 2;
}
//Write a function that returns true if the given number is the multiplication of 3 prime numbers
// and false otherwise.
// Knowing that (a) is less then 100. 
// Example:
// >>> is_multiply_prime(30)
// true
// 30 = 2 * 3 * 5
function is_multiply_prime(a){
    let count = 0;
    for(let i = 2; i <= a; i++){
        if(a % i == 0){
            count++;
            while(a % i == 0){
                a /= i;
            }
        }
    }
    return count == 3;
}
