// >>> decimal_to_binary(32)
// "db100000db"
function decimal_to_binary(decimal){
  let binary = "";
  while (decimal > 0) {
    binary = (decimal % 2) + binary;
    decimal = Math.floor(decimal / 2);
  }
  return "db" + binary + "db";
}

