//Given a positive integer N, return the total sum of its digits in binary.
// Example
// >>> solve(1000)
// "1"
// >>> solve(150)
// "110"
// >>> solve(147)
// "1100"
// Variables:
// @N integer
// Constraints: 0 ≤ N ≤ 10000.
// Output:
// a string of binary number
function solve(N){
    let sum = 0;
    while(N > 0){
        sum += N % 10;
        N = Math.floor(N / 10);
    }
    return sum.toString(2);
}

