//Create a function encrypt that takes a string as an argument and
// returns a string encrypted with the alphabet being rotated. 
// The alphabet should be rotated in a manner such that the letters 
// shift down by two multiplied to two places.
// For example:
// >>> encrypt("hi")
// "lm"
// >>> encrypt("asdfghjkl")
// "ewhjklnop"
// >>> encrypt("gf")
// "kj"
// >>> encrypt("et")
// "ix"
function encrypt(s){
    let result = '';
    for(let i = 0; i < s.length; i++){
        let charCode = s.charCodeAt(i);
        if(charCode >= 97 && charCode <= 122){
            result += String.fromCharCode((charCode - 97 + 4) % 26 + 97);
        } else if(charCode >= 65 && charCode <= 90){
            result += String.fromCharCode((charCode - 65 + 4) % 26 + 65);
        } else {
            result += s[i];
        }
    }
    return result;
}

//Create a function encrypt that takes a string as an argument and
// returns a string encrypted with the alphabet being rotated. 
// The alphabet should be rotated in a manner such that the letters 
// shift down by two multiplied to two places.
// For example:
// >>> encrypt("hi")
// "lm"
// >>> encrypt("asdfghjkl")
// "ewhjklnop"
// >>> encrypt("gf")
// "kj"
// >>> encrypt("et")
// "ix"
function encrypt(s){
    let result = '';
    for(let i = 0; i < s.length; i++){
        let charCode = s.charCodeAt(i) + 4;
        if(charCode > 122){
            charCode -= 26;
        }
        result += String.fromCharCode(charCode);
    }
    return result;
}

