// 11
// >>> skjkasdkd([0, 81, 12, 3, 1, 21])
// 3
// >>> skjkasdkd([0, 8, 1, 2, 1, 7])
// 7
function skjkasdkd(lst){
    let maxPrime = 0;
    for(let i = 0; i < lst.length; i++){
        if(isPrime(lst[i]) && lst[i] > maxPrime){
            maxPrime = lst[i];
        }
    }
    return maxPrime.toString().split('').reduce((a, b) => parseInt(a) + parseInt(b), 0);
}
