//Given a string s, count the number of uppercase vowels in even indices.
// For example:
// >>> count_upper("aBCdEf")
// 1
// >>> count_upper("abcdefg")
// 0
// >>> count_upper("dBBE")
// 0
function count_upper(s){
    let count = 0;
    for(let i = 0; i < s.length; i += 2){
        if('AEIOU'.includes(s[i])){
            count++;
        }
    }
    return count;
}

