// >>> exchange([1, 2, 3, 4], [1, 5, 3, 4])
// "NO"
// It is assumed that the input arrays will be non-empty.
function exchange(lst1, lst2){
    let oddCount = 0;
    let evenCount = 0;
    for(let i = 0; i < lst1.length; i++){
        if(lst1[i] % 2 !== 0){
            oddCount++;
        }
    }
    for(let i = 0; i < lst2.length; i++){
        if(lst2[i] % 2 === 0){
            evenCount++;
        }
    }
    return oddCount <= evenCount ? "YES" : "NO";
}

