//Given a string representing a space separated lowercase letters, return an object
// of the letter with the most repetition and containing the corresponding count.
// If several letters have the same occurrence, return all of them.
// Example:
// >>> histogram("a b c")
// {"a": 1, "b": 1, "c": 1}
// >>> histogram("a b b a")
// {"a": 2, "b": 2}
// >>> histogram("a b c a b")
// {"a": 2, "b": 2}
// >>> histogram("b b b b a")
// {"b": 4}
// >>> histogram("")
// {}
function histogram(test){
  let result = {};
  let max = 0;
  let letters = test.split(' ').filter(Boolean);
  letters.forEach(letter => {
    result[letter] = (result[letter] || 0) + 1;
    max = Math.max(max, result[letter]);
  });
  return Object.fromEntries(Object.entries(result).filter(([_, count]) => count === max));
}
