// >>> sort_array([1, 0, 2, 3, 4])
// [0, 1, 2, 3, 4]
function sort_array(arr){
  return arr.sort((a,b) => {
    let aOnes = a.toString(2).split('').filter(c => c === '1').length;
    let bOnes = b.toString(2).split('').filter(c => c === '1').length;
    if (aOnes === bOnes) {
      return a - b;
    } else {
      return aOnes - bOnes;
    }
  });
}

