// ""
function get_closest_vowel(word){
    let vowels = "aeiouAEIOU";
    let result = "";
    for(let i = word.length - 2; i > 0; i--){
        if(vowels.includes(word[i]) && !vowels.includes(word[i + 1]) && !vowels.includes(word[i - 1])){
            result = word[i];
            break;
        }
    }
    return result;
}

