function maximum(arr, k){
    let maxArr = [];
    for(let i = 0; i < arr.length; i++){
        for(let j = i + 1; j < arr.length; j++){
            if(arr[i] < arr[j]){
                let temp = arr[i];
                arr[i] = arr[j];
                arr[j] = temp;
            }
        }
    }
    for(let i = 0; i < k; i++){
        maxArr.push(arr[i]);
    }
    return maxArr;
}

