function intersection(interval1, interval2){
    let start = Math.max(interval1[0], interval2[0]);
    let end = Math.min(interval1[1], interval2[1]);
    if (start > end) {
        return "NO";
    }
    let length = end - start + 1;
    return isPrime(length) ? "YES" : "NO";
}
