// tri(2) = 1 + (2 / 2) = 2
// tri(4) = 3
// tri(3) = tri(2) + tri(1) + tri(4)
// = 2 + 3 + 3 = 8 
// You are given a non-negative integer number n, you have to a return an array of the 
// first n + 1 numbers of the Tribonacci sequence.
// Examples:
// >>> tri(3)
// [1, 3, 2, 8]
function tri(n){
    let arr = [3, 2];
    for (let i = 2; i <= n; i++) {
        if (i % 2 === 0) {
            arr.push(1 + i / 2);
        } else {
            arr.push(arr[i - 1] + arr[i - 2] + arr[i + 1]);
        }
    }
    return arr;
}

