//Create a function that takes a string as input which contains only square brackets.
// The function should return true if and only if there is a valid subsequence of brackets 
// where at least one bracket in the subsequence is nested.
// >>> is_nested("[[]]")
// true
// >>> is_nested("[]]]]]]][[[[[]")
// false
// >>> is_nested("[][]")
// false
// >>> is_nested("[]")
// false
// >>> is_nested("[[][]]")
// true
// >>> is_nested("[[]][[")
// true
function is_nested(string){
    let count = 0;
    for(let i = 0; i < string.length; i++){
        if(string[i] === '['){
            count++;
        } else if(string[i] === ']'){
            count--;
        }
        if(count < 0){
            return false;
        }
    }
    return count !== 0;
}

//Create a function that takes a string as input which contains only square brackets.
// The function should return true if and only if there is a valid subsequence of brackets 
// where at least one bracket in the subsequence is nested.
// >>> is_nested("[[]]")
// true
// >>> is_nested("[]]]]]]][[[[[]")
// false
// >>> is_nested("[][]")
// false
// >>> is_nested("[]")
// false
// >>> is_nested("[[][]]")
// true
// >>> is_nested("[[]][[")
// true
function is_nested(string){
    let stack = [];
    for(let i = 0; i < string.length; i++){
        if(string[i] === '['){
            stack.push(string[i]);
        } else if(string[i] === ']' && stack.length > 0){
            stack.pop();
        }
    }
    return stack.length > 0;
}

