//Given a string text, replace all spaces in it with underscores, 
// and if a string has more than 2 consecutive spaces, 
// then replace all consecutive spaces with - 
// >>> fix_spaces(" Example")
// "Example"
// >>> fix_spaces(" Example 1")
// "Example_1"
// >>> fix_spaces(" Example 2")
// "_Example_2"
// >>> fix_spaces(" Example 3")
// "_Example-3"
function fix_spaces(text){
    return text.replace(/ /g, "_").replace(/__+/g, "-");
}

//Given a string text, replace all spaces in it with underscores, 
// and if a string has more than 2 consecutive spaces, 
// then replace all consecutive spaces with - 
// >>> fix_spaces(" Example")
// "Example"
// >>> fix_spaces(" Example 1")
// "Example_1"
// >>> fix_spaces(" Example 2")
// "_Example_2"
// >>> fix_spaces(" Example 3")
// "_Example-3"
function fix_spaces(text){
    let result = "";
    let count = 0;
    for(let i = 0; i < text.length; i++){
        if(text[i] === " "){
            count++;
        }else{
            if(count === 1){
                result += "_";
            }else if(count > 1){
                result += "-";
            }
            count = 0;
            result += text[i];
        }
    }
    if(count === 1){
        result += "_";
    }else if(count > 1){
        result += "-";
    }
    return result;
}

