// - The substring after the dot should be one of these: ['txt', 'exe', 'dll']
// Examples:
// >>> file_name_check("example.txt")
// "Yes"
// >>> file_name_check("1example.dll")
// "No"
function file_name_check(file_name){
    let count = 0;
    for(let i = 0; i < file_name.length; i++){
        if(file_name[i] >= '0' && file_name[i] <= '9'){
            count++;
        }
    }
    if(count > 3 || file_name.indexOf('.') === -1 || file_name[0] < 'a' || file_name[0] > 'z' && file_name[0] < 'A' || file_name[0] > 'Z' || file_name.slice(file_name.indexOf('.') + 1) !== 'txt' && file_name.slice(file_name.indexOf('.') + 1) !== 'exe' && file_name.slice(file_name.indexOf('.') + 1) !== 'dll'){
        return 'No';
    }
    return 'Yes';
}

//Create a function which takes a string representing a file's name, and returns
