    if(count > 3 || dot_count != 1 || file_name[0] < 'a' || file_name[0] > 'z' || file_name[0] < 'A' || file_name[0] > 'Z' || !valid_extensions.includes(extension)){
        return "No";
    }
    return "Yes";
}

