// "No"
function file_name_check(file_name){
    let validExtensions = ['txt', 'exe', 'dll'];
    let dotIndex = file_name.indexOf('.');
    let namePart = file_name.substring(0, dotIndex);
    let extensionPart = file_name.substring(dotIndex + 1);
