// >>> order_by_points([1, 11, -1, -11, -12])
// [-1, -11, 1, -12, 11]
// >>> order_by_points([])
// []
function order_by_points(nums){
    return nums.sort((a, b) => {
        let sumA = a.toString().split('').reduce((sum, digit) => sum + Math.abs(parseInt(digit)), 0);
        let sumB = b.toString().split('').reduce((sum, digit) => sum + Math.abs(parseInt(digit)), 0);
        if (sumA === sumB) {
