// Explanation: 
// a = [1, 3, 7, 13, 21]
// The only valid triple is (1, 7, 13).
function get_max_triples(n){
    let a = [];
    for(let i = 1; i <= n; i++){
        a.push(i * i - i + 1);
    }
    let count = 0;
    for(let i = 0; i < a.length; i++){
        for(let j = i + 1; j < a.length; j++){
            for(let k = j + 1; k < a.length; k++){
                if((a[i] + a[j] + a[k]) % 3 === 0){
                    count++;
                }
            }
        }
    }
    return count;
}

