//A simple program which should return the value of x if n is 
// a prime number and should return the value of y otherwise.
// Examples:
// >>> x_or_y(7, 34, 12)
// 34
// >>> x_or_y(15, 8, 5)
// 5
function x_or_y(n, x, y){
    let isPrime = true;
    for(let i = 2; i < n; i++){
        if(n % i === 0){
            isPrime = false;
            break;
        }
    }
    return isPrime ? x : y;
}
//A simple program which should return the value of x if n is 
// a prime number and should return the value of y otherwise.
// Examples:
// >>> x_or_y(7, 34, 12)
// 34
// >>> x_or_y(15, 8, 5)
// 5
function x_or_y(n, x, y){
    // Check if n is a prime number
    for(let i = 2; i <= Math.sqrt(n); i++){
        if(n % i === 0){
            return y;
        }
    }
    return x;
}
