//Given an integer. return an array that has the number of even and odd digits respectively.
// Example:
// >>> even_odd_count(-12)
// [1, 1]
// >>> even_odd_count(123)
// [1, 2]
function even_odd_count(num){
    let even = 0;
    let odd = 0;
    let numArr = Math.abs(num).toString().split('');
    for(let i = 0; i < numArr.length; i++){
        if(numArr[i] % 2 === 0){
            even++;
        } else {
            odd++;
        }
    }
    return [even, odd];
}
