// >>> even_odd_count(-12)
// [1, 1]
// >>> even_odd_count(123)
// [1, 2]
function even_odd_count(num){
  let count = [0, 0];
  let str = Math.abs(num).toString();
  for(let i = 0; i < str.length; i++){
    if(str[i] % 2 === 0){
      count[0]++;
    } else {
      count[1]++;
    }
  }
  return count;
}
