// true
// >>> right_angle_triangle(1, 2, 3)
// false
function right_angle_triangle(a, b, c){
    //first sort the numbers in descending order
    let nums = [a, b, c].sort((a, b) => b - a);
    //then check if the square of the largest number is equal to the sum of the squares of the other two
    return nums[0] * nums[0] === nums[1] * nums[1] + nums[2] * nums[2];
}

//Write a function that accepts an array of strings.
// The array contains different words. Return the word with maximum number
// of unique characters. If multiple strings have maximum number of unique
// characters, return the one which comes first in lexicographical order.
// >>> find_max(["name", "of", "string"])
// "string"
// >>> find_max(["name", "enam", "game"])
// "enam"
// >>> find_max(["aaaaaaa", "bb", "cc"])
// "aaaaaaa"
function find_max(words){
    let max = 0;
    let maxWord = "";
    for(let i = 0; i < words.length; i++){
        let uniqueChars = new Set(words[i]).size;
        if(uniqueChars > max){
            max = uniqueChars;
            maxWord = words[i];
        }
        else if(uniqueChars === max){
            if(words[i] < maxWord){
                maxWord = words[i];
            }
        }
    }
    return maxWord;
}

