function find_max(words){
    let max = 0;
    let maxWord = "";
    for(let i = 0; i < words.length; i++){
        let uniqueChars = new Set(words[i]).size;
        if(uniqueChars > max){
            max = uniqueChars;
            maxWord = words[i];
        }else if(uniqueChars === max){
            if(words[i] < maxWord){
                maxWord = words[i];
            }
        }
    }
    return maxWord;
}

