//prime_fib returns n-th number that is a Fibonacci number and it's also prime.
// >>> prime_fib(1)
// 2
// >>> prime_fib(2)
// 3
// >>> prime_fib(3)
// 5
// >>> prime_fib(4)
// 13
// >>> prime_fib(5)
// 89
function prime_fib(n){
    let fib = [0, 1];
    let prime = [2];
    let i = 2;
    while(prime.length < n){
        fib[i] = fib[i-1] + fib[i-2];
        if(isPrime(fib[i])){
            prime.push(fib[i]);
        }
        i++;
    }
    return prime[n-1];
}
//prime_fib returns n-th number that is a Fibonacci number and it's also prime.
// >>> prime_fib(1)
// 2
// >>> prime_fib(2)
// 3
// >>> prime_fib(3)
// 5
// >>> prime_fib(4)
// 13
// >>> prime_fib(5)
// 89
function prime_fib(n){
    let fib = [0, 1];
    let prime = [];
    for(let i = 2; i < 1000; i++){
        fib[i] = fib[i - 1] + fib[i - 2];
        if(isPrime(fib[i])){
            prime.push(fib[i]);
        }
    }
    return prime[n - 1];
}
//prime_fib returns n-th number that is a Fibonacci number and it's also prime.
// >>> prime_fib(1)
// 2
// >>> prime_fib(2)
// 3
// >>> prime_fib(3)
// 5
// >>> prime_fib(4)
// 13
// >>> prime_fib(5)
// 89
function prime_fib(n){
    let fib = [0, 1];
    let i = 2;
    while(fib.length < n*2){
        fib.push(fib[i-1] + fib[i-2]);
        i++;
    }
    let prime = [];
    for(let j = 0; j < fib.length; j++){
        if(isPrime(fib[j])){
            prime.push(fib[j]);
        }
    }
    return prime[n-1];
}
