// >>> triples_sum_to_zero([2, 4, -5, 3, 9, 7])
// true
// >>> triples_sum_to_zero([1])
// false
function triples_sum_to_zero(l){
    let n = l.length;
    for (let i = 0; i < n - 1; i++) {
        let s = new Set();
        for (let j = i + 1; j < n; j++) {
            let x = -(l[i] + l[j]);
            if (s.has(x))
                return true;
            s.add(l[j]);
        }
    }
    return false;
}
