//Return median of elements in the array l.
// >>> median([3, 1, 2, 4, 5])
// 3
// >>> median([-10, 4, 6, 1000, 10, 20])
// 15.0
function median(l){
    l.sort(function(a, b){return a - b});
    var half = Math.floor(l.length/2);
    if(l.length % 2){
        return l[half];
    }else{
        return (l[half-1] + l[half]) / 2.0;
    }
}
